mapper.select:
SELECT A.BBS_NO, A.CATE, B.NAME CATE_NAME, A.TITLE, A.CONTENT, A.STATUS, CASE WHEN A.STATUS = 'S' THEN '공개' ELSE '비공개' END AS STATUS_NAME, A.FIX_YN, A.CDATE, A.UUSER, CASE WHEN A.UUSER > 2000000000 THEN (SELECT ADMIN_ID FROM T_ADMIN WHERE ADMIN_NO = A.UUSER) ELSE (SELECT MEM_ID FROM T_MEMBER WHERE MEM_NO = A.UUSER) END AS UUSER_ID, CASE WHEN A.UUSER > 2000000000 THEN '마스터' ELSE (SELECT NAME FROM T_MEMBER WHERE MEM_NO = A.UUSER) END AS UUSER_NAME, A.UDATE FROM T_BBS A INNER JOIN T_CODE B ON A.CATE = CONCAT(B.CODE1, B.CODE2) WHERE A.STATUS = 'S' AND A.FIX_YN = 'Y' UNION ALL SELECT A.BBS_NO, A.CATE, B.NAME CATE_NAME, A.TITLE, A.CONTENT, A.STATUS, CASE WHEN A.STATUS = 'S' THEN '공개' ELSE '비공개' END AS STATUS_NAME, A.FIX_YN, A.CDATE, A.UUSER, CASE WHEN A.UUSER > 2000000000 THEN (SELECT ADMIN_ID FROM T_ADMIN WHERE ADMIN_NO = A.UUSER) ELSE (SELECT MEM_ID FROM T_MEMBER WHERE MEM_NO = A.UUSER) END AS UUSER_ID, CASE WHEN A.UUSER > 2000000000 THEN '마스터' ELSE (SELECT NAME FROM T_MEMBER WHERE MEM_NO = A.UUSER) END AS UUSER_NAME, A.UDATE FROM T_BBS A INNER JOIN T_CODE B ON A.CATE = CONCAT(B.CODE1, B.CODE2) WHERE (A.STATUS = 'S' OR (A.STATUS = 'H' AND A.CUSER = #{cuser})) AND A.FIX_YN = 'N'
AND A.TITLE LIKE CONCAT('%', #{title}, '%')
AND A.STATUS = #{status}
AND A.CATE = #{cate}
AND A.FIX_YN = #{fixYn}
AND A.CDATE >= #{fromCdate}
AND A.CDATE < DATE_ADD(#{toCdate}, INTERVAL 1 DAY)
AND ( A.UUSER IN (SELECT MEM_NO FROM T_MEMBER WHERE NAME LIKE CONCAT('%', #{userName}, '%') OR MEM_ID LIKE CONCAT('%', #{userName}, '%') ) OR A.CUSER IN (SELECT ADMIN_NO FROM T_ADMIN WHERE ADMIN_ID LIKE CONCAT('%', #{userName}, '%') ) )
ORDER BY FIX_YN DESC, BBS_NO DESC LIMIT #{limit} OFFSET #{offset}

SELECT SUM(CNT) FROM ( SELECT COUNT(*) CNT FROM T_BBS A WHERE A.STATUS = 'S' AND A.FIX_YN = 'Y' UNION ALL SELECT COUNT(*) CNT FROM T_BBS A WHERE (A.STATUS = 'S' OR (A.STATUS = 'H' AND A.CUSER = #{cuser})) AND A.FIX_YN = 'N'
AND A.TITLE LIKE CONCAT('%', #{title}, '%')
AND A.STATUS = #{status}
AND A.CATE = #{cate}
AND A.CDATE >= #{fromCdate}
AND A.CDATE < DATE_ADD(#{toCdate}, INTERVAL 1 DAY)
AND ( A.UUSER IN (SELECT MEM_NO FROM T_MEMBER WHERE NAME LIKE CONCAT('%', #{userName}, '%') OR MEM_ID LIKE CONCAT('%', #{userName}, '%') ) OR A.CUSER IN (SELECT ADMIN_NO FROM T_ADMIN WHERE ADMIN_ID LIKE CONCAT('%', #{userName}, '%') ) )
) C

mapper.update:
UPDATE T_BBS_COMMENT
   SET COMMENT		= #{comment},
	   UUSER		= #{cuser},
	   UDATE		= NOW()
 WHERE BBS_NO	= #{bbsNo}
   AND COMMENT_NO	= #{commentNo}

UPDATE T_BBS_COMMENT
   SET STATUS		= #{status},
	   UUSER		= #{cuser},
	   UDATE		= NOW()
 WHERE BBS_NO	= #{bbsNo}
   AND COMMENT_NO	= #{commentNo}

mapper.insert:
INSERT INTO T_BBS_IMG (
	BBS_NO,
	ATTACH,
	ATTACH_ORG_NAME
) VALUES (
	#{bbsNo},
	#{attach},
	#{attachOrgName}
)
